import _ from "lodash";
import React, { Component } from "react";
import moment from "moment";
import { connect } from "react-redux";
import { fetch_transactions } from "../../actions";
import NetSpendSnapshot from "./NetSpendSnapshot";
import SpendCategorySnapshot from "./SpendCategorySnapshot";
import BankingInfoSnapshot from "./BankingInfoSnapshot";
import DateRange from "../DateRange";
import Greeting from "../Greeting";

class Snapshots extends Component {
  componentDidMount() {
    this.props.fetchTransactions();
  }

  render() {
    return (
      <div>
        <Greeting />
        <DateRange />
        <NetSpendSnapshot
          onDateChange={this.onChangeDate}
          transactions={this.props.transactions}
        />
        <SpendCategorySnapshot
          onDateChange={this.onChangeDate}
          transactions={this.props.transactions}
        />
        <BankingInfoSnapshot
          onDateChange={this.onChangeDate}
          transactions={this.props.transactions}
        />
      </div>
    );
  }
}

const mapStateToProps = state => {
  return {
    transactions: Object.values(state.transactions)
  };
};

export default connect(
  mapStateToProps,
  { fetch_transactions }
)(Snapshots);
