import _ from "lodash";
import React, { Component, Fragment } from "react";
import { connect } from "react-redux";
import { fetchTransactions } from "../actions";

// To do refactor this component to be the page that renders all of the cards with the data. For instance spending card, networth card, graph of increase in spend etc.

class Snapshot extends Component {
  componentDidMount() {
    this.props.fetchTransactions();
  }

  render() {
    const { transactions } = this.props;

    const income = _.chain(transactions)
      .filter(transaction => {
        if (transaction.category === "Paycheck") return transaction;
      })
      .map(transaction => {
        return transaction.amount;
      })
      .sum()
      .value();

    const expenditure = _.chain(transactions)
      .filter(transaction => {
        if (transaction.category === "Credit Card Payment")
          return transaction.amount;
      })
      .sum()
      .value();

    console.log(expenditure);
    const name = "Kyle";

    return (
      <Fragment>
        <div>
          <h2>Hi {name}, here's your financial snapshot!</h2>
        </div>
        <div className="ui card">
          <div className="content">
            <div className="header">Net Spend</div>
          </div>
          <div className="content">
            <h4 className="ui sub header">Income: ${income.toFixed(2)}</h4>
            <h4 className="ui sub header">Spent: ${expenditure} </h4>
            <h4 className="ui sub header">Net: {income - expenditure}</h4>
          </div>
        </div>
      </Fragment>
    );
  }
}

const mapStateToProps = state => {
  return {
    transactions: state.transactions
  };
};

export default connect(
  mapStateToProps,
  { fetchTransactions }
)(Snapshot);
