  name = "Kyle";

  render() {
    return (
      <Fragment>
        <div>
          <h2>Hi {name}, here's your financial snapshot!</h2>
        </div>
        <div className="ui card">
          <div className="content">
            <div className="header">Net Spend</div>
          </div>
          <div className="content">
            <h4 className="ui sub header">
              Income: ${this.calculateIncome(this.props.transactions)}
            </h4>
            <h4 className="ui sub header">Spent: ${expenditure} </h4>
            <h4 className="ui sub header">Net: {income - expenditure}</h4>
          </div>
        </div>
      </Fragment>
    );
  }
}

const mapStateToProps = state => {
  return {
    transactions: Object.values(state.transactions) // this converts our transactions objects into arrays.
  };
};

export default connect(
  mapStateToProps,
  { fetchTransactions }
)(Snapshot);
