import React, { Component } from "react";
import { connect } from "react-redux";
import TransactionItem from "./TransactionItem";
import Spinner from "../Spinner";
import { fetchTransactions } from "../../actions";
import Pagination from "../Pagination";

// DON'T FORGET TO SPECIFY THIS. WHEN CALLING FUNCTIONS

class TransactionList extends Component {
  state = {
    allTransactions: [],
    currentTransactions: [],
    currentPage: null,
    totalPages: null
  };

  componentDidMount() {
    this.props.fetchTransactions();
    this.setState({ allTransactions: this.props.transactions });
  }

  onPageChanged = data => {
    const { transactions } = this.props;
    const { currentPage, totalPages, pageLimit } = data;

    const offset = (currentPage - 1) * pageLimit;
    const currentTransactions = transactions.slice(offset, offset + pageLimit);

    this.setState({ currentPage, currentTransactions, totalPages });
  };

  renderTransactions() {
    return this.props.transactions.map(
      ({ date, description, category, amount, id }) => {
        return (
          <TransactionItem
            date={date}
            description={description}
            category={category}
            amount={amount}
            key={id}
            id={id}
          />
        );
      }
    );
  }

  render() {
    console.log(this.props.transactionso);
    const { currentTransactions, currentPage, totalPages } = this.state;
    const { transactions } = this.props;
    const totalTransactions = this.props.transactions.length;

    if (totalTransactions === 0) {
      return <Spinner />;
    }

    return (
      <div>
        <table className="ui celled table">
          <thead>
            <tr>
              <th>Date</th>
              <th>Description</th>
              <th>Category</th>
              <th>Amount</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>{this.renderTransactions()}</tbody>
        </table>
        <Pagination
          totalRecords={totalTransactions}
          pageLimit={18}
          pageNeighbours={1}
          onPageChanged={this.onPageChanged}
        />
      </div>
    );
  }
}

const mapStateToProps = state => {
  return {
    transactions: Object.values(state.transactions)
  };
};

export default connect(
  mapStateToProps,
  { fetchTransactions }
)(TransactionList);
