import React, { Component } from "react";
import { connect } from "react-redux";
import { fetchTransaction } from "../../actions";
import Modal from "../Modal";

class TransactionDetail extends Component {
  componentDidMount() {
    this.props.fetchTransaction(this.props.match.params.id);
  }

  renderContent() {
    if (!this.props.transaction) {
      return "Could not get transaction details";
    }

    return `Date of purchase: ${this.props.transaction.date}
            Description: ${this.props.transaction.description}
            Category: ${this.props.transaction.category}
            Amount: ${this.props.transaction.amount}
    `;
  }

  render() {
    return (
      <Modal
        header="Transaction Details"
        content={`Date of purchase =`}
        actions=""
      />
    );
  }
}

const mapStateToProps = (state, ownProps) => {
  return {
    transaction: state.transaction[ownProps.match.parans.id]
  };
};
export default connect(
  mapStateToProps,
  { fetchTransaction }
)(TransactionDetail);
