import React, { Component } from "react";

class DateRange extends Component {
  handleDateChange(date) {
    this.props.changeRange(date);
  }

  render() {
    return (
      <div className="ui buttons">
        <button
          onClick={today => this.handleDateChange(today)}
          className="ui button"
        >
          Day
        </button>
        <button
          onClick={oneWeekAgo => this.handleDateChange(oneWeekAgo)}
          className="ui button"
        >
          Week
        </button>
        <button
          onClick={oneMonthAgo => this.handleDateChange(oneMonthAgo)}
          className="ui button"
        >
          Month
        </button>
        <button
          onClick={oneYearAgo => this.handleDateChange(oneYearAgo)}
          className="ui button"
        >
          Year
        </button>
      </div>
    );
  }
}

export default DateRange;
