import React, { Component } from "react";
import { connect } from "react-redux";
import { fetchTransactions } from "../../actions";
import { NetSpendSnapshot } from "./NetSpendSnapshot";
import SpendCategorySnapshot from "./SpendCategorySnapshot";
import BankingInfoSnapshot from "./BankingInfoSnapshot";
import DateRange from "../DateRange";
import Greeting from "../Greeting";
import {
  calculateIncome,
  calculateExpenses,
  calculateNet,
  dateRanges
} from "../../helpers/snapshotHelpers";

class Snapshots extends Component {
  componentDidMount() {
    this.props.fetchTransactions();
  }

  state = { dateRange: dateRanges.oneMonthAgo };

  handleDateChange(date) {
    this.setState({ dateRange: date });
  }

  render() {
    const income = calculateIncome(
      this.props.transactions,
      this.state.dateRange
    );

    const expenses = calculateExpenses(
      this.props.transactions,
      this.state.dateRange
    );

    const net = calculateNet(this.props.transactions);

    return (
      <div>
        <Greeting />
        <button
          onClick={() => this.handleDateChange(dateRanges.oneYearAgo)}
          className="ui button"
        >
          Year
        </button>
        <NetSpendSnapshot
          transactions={this.props.transactions}
          income={income}
          expenses={expenses}
          net={net}
        />
        <SpendCategorySnapshot transactions={this.props.transactions} />
        <BankingInfoSnapshot />
      </div>
    );
  }
}

const mapStateToProps = state => {
  return {
    transactions: Object.values(state.transactions)
  };
};

export default connect(
  mapStateToProps,
  { fetchTransactions }
)(Snapshots);
