import _ from "lodash";
import React, { Component } from "react";
import { connect } from "react-redux";
import TransactionItem from "./TransactionItem";
import Spinner from "../Spinner";
import { fetchTransactions } from "../../actions";
import Pagination from "../Pagination";

// DON'T FORGET TO SPECIFY THIS. WHEN CALLING FUNCTIONS

class TransactionList extends Component {
  state = {
    currentTransactions: [],
    currentPage: null,
    totalPages: null
  };

  componentDidMount() {
    this.props.fetchTransactions();
  }

  onPageChanged = data => {
    const { transactions } = this.props;
    const { currentPage, totalPages, pageLimit } = data;

    //The offset value indicates the starting index for fetching the records for the current page. Using (currentPage - 1) ensures that the offset is zero-based.
    const offset = (currentPage - 1) * pageLimit;
    // We use the Array.prototype.slice() method to extract the required chunk of records from allCountries by passing the offset as the starting index for the slice
    // and (offset + pageLimit) as the index before which to end the slice.
    const currentTransactions = transactions.slice(offset, offset + pageLimit);

    this.setState({
      currentPage,
      currentTransactions,
      totalPages
    });
  };

  renderTransactions() {
    return this.state.currentTransactions.map(
      ({ date, description, category, amount, id }) => {
        return (
          <TransactionItem
            date={date}
            description={description}
            category={category}
            amount={amount}
            key={id}
            id={id}
          />
        );
      }
    );
  }

  handleSort = transactions => {
    const sortedArray = _.orderBy(transactions, ["date"], ["asc"]);
    return sortedArray;
  };

  render() {
    console.log(this.handleSort(this.props.transactions));
    const totalTransactions = this.props.transactions.length;

    if (totalTransactions === 0) {
      return <Spinner />;
    }

    return (
      <div>
        <h2 style={{ marginTop: "10px" }}>
          <strong className="text-secondary">{totalTransactions}</strong>
          &nbsp; Transactions
        </h2>
        <table className="ui celled table">
          <thead>
            <tr>
              <th onClick={this.handleSort}>Date</th>
              <th>Description</th>
              <th>Category</th>
              <th>Amount</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>{this.renderTransactions()}</tbody>
        </table>
        <div>
          <Pagination
            totalRecords={totalTransactions}
            pageLimit={10}
            pageNeighbours={1}
            onPageChanged={this.onPageChanged}
          />
        </div>
      </div>
    );
  }
}

const mapStateToProps = state => {
  return {
    transactions: Object.values(state.transactions)
  };
};

export default connect(
  mapStateToProps,
  { fetchTransactions }
)(TransactionList);
