import _ from "lodash";
import moment from "moment";

export const calculateIncome = transactions => {
  const income = _.chain(transactions)
    .filter(transaction => {
      const todaysDate = moment().format("MM/DD/YYYY");
      const oneWeekAgo = moment()
        .subtract(7, "days")
        .calendar();
      const oneMonthAgo = moment()
        .subtract(30, "days")
        .calendar();
      const oneYearAgo = moment()
        .subtract(365, "days")
        .calendar();
      if (
        transaction.category === "Paycheck" &&
        moment(transaction.date).isAfter(oneMonthAgo)
      )
        return transaction;
    })
    .map(transaction => {
      return transaction.amount;
    })
    .sum()
    .value();

  return income.toFixed(2);
};

export const calculateExpenses = transactions => {
  // TO DO find a way to refactor;
  const toRemove = [];

  const todaysDate = moment().format("MM/DD/YYYY");
  const oneWeekAgo = moment()
    .subtract(7, "days")
    .calendar();
  const oneMonthAgo = moment()
    .subtract(30, "days")
    .calendar();
  const oneYearAgo = moment()
    .subtract(365, "days")
    .calendar();

  _.map(transactions, transaction => {
    if (
      transaction.category === "Credit Card Payment" ||
      transaction.category === "Transfer"
    ) {
      toRemove.push(transaction);
    }
  });

  const filteredTransactions = _.filter(transactions, transaction => {
    return !toRemove.includes(transaction);
  });

  const totalSpent = _.chain(filteredTransactions)

    .map(transaction => {
      if (moment(transaction.date).isAfter(oneMonthAgo)) {
        return transaction.amount;
      }
    })
    .sum()
    .value();

  return totalSpent.toFixed(2);
};
